%m234.m         单层感知器(单神经元)一次学习(训练)
clear all;
close all;

net=newp([-2 2;-2 2],1); %构建2输入、单输出单层感知器
w0=[1 -0.8]              %设感知器的权值、阈值
b0=0.1                       
net.iw{1,1}=w0;           
net.b{1}=b0;
u=[1;2]                   %观测输入、输出样本                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
d=[1]                      
y=sim(net,u)              %观测感知器的输出、输出误差  
e=d-y                        
dw=learnp(w0,u,[],[],[],[],e,[],[],[]) %一次学习后，观测权系值调整量  
db=e
